package loops;

import java.util.Scanner;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		System.out.println("Enter a number -");
		int k = sc.nextInt();
		
		for(int i=0; i<=10; i++)
		{
			
			System.out.println(i*k);
		}
		
		
		
	}

}
